DROP TABLE IF EXISTS addresses;
CREATE TABLE addresses
(
    id     INTEGER NOT NULL PRIMARY KEY AUTOINCREMENT,
    street varchar(255)
);


DROP TABLE IF EXISTS departments;
CREATE TABLE departments
(
    id   INTEGER NOT NULL PRIMARY KEY AUTOINCREMENT,
    name VARCHAR(255)
);

DROP TABLE IF EXISTS foos;
CREATE TABLE foos
(
    id   INTEGER NOT NULL PRIMARY KEY AUTOINCREMENT,
    name VARCHAR(255)
);